    { id: '1', title: 'First Post!', content: 'Hello!' },
    { id: '2', title: 'Second Post', content: 'More text' }
];

export const postsSlice  = createSlice({
    name : 'posts',
    initialState,
    reducers:{
        postAdded: (state, action) => {
            state.push(action.payload)
